const woorden=document.querySelector(".woorden"),start=document.querySelector("#start"),ion=document.querySelector("#ion"),punt=document.querySelector(".punten");let score=document.querySelector(".score");const lichtbox=document.querySelector(".lichtbox");let newScore=0;const ionen=["nitraat","sulfaat","fosfaat","hydroxide","jodide","bromide","chloride","sulfide","oxide","zilver","aluminium","barium","calcium","koper","ijzer_2","ijzer_3","kwik","kalium","magnesium","natrium","ammonium","lood","tin","zink"];let bezoekers=document.querySelector(".counter");const getShuffledArr=e=>{const n=e.slice();for(let e=n.length-1;e>0;e--){const t=Math.floor(Math.random()*(e+1));[n[e],n[t]]=[n[t],n[e]]}return n};let ionenrij=getShuffledArr(ionen);ionenrij.forEach(e=>{woorden.innerHTML+=`<button id='${e}' onclick='test(${e})'>${e}</button>`});const counter=async()=>{const e=await fetch("../../api/hitCounter.php",{method:"GET"}),n=await e.text();bezoekers.innerHTML=`U bent de <b class='num'>${n}</b>ste bezoeker`};counter(),ion.innerHTML="<h1>Klik op start</h1>",start.addEventListener("click",()=>{let e=ionen[Math.floor(Math.random()*ionen.length)];localStorage.setItem("ionnaam",e),lichtbox.style.display="none";const n=localStorage.getItem("ionnaam");ion.innerHTML=`<img src="./images/${n}.png" alt="${n}">`,score.innerHTML=""});const test=e=>{let n=e.innerHTML;console.log(n),lichtbox.style.display="flex",n===localStorage.getItem("ionnaam")?(newScore++,score.innerHTML="Goed punt erbij"):(score.innerHTML="Foutje puntje eraf",newScore--),start.innerHTML="Volgende",punt.innerHTML=`${newScore} punten`};